mapper.update:
update board 
set title= #{board.title},
	content=#{board.content},
	state = #{board.state}
where id = #{board.id}

mapper.select:

     select count(id) from board
        
       where title like concat('%', #{cri.search}, '%')
     
     	
       where content like concat('%', #{cri.search}, '%')
     
      
       where writer = #{cri.search}
     
      
       where id = #{cri.search}
     
     ;


     select * from board
        
       where title like concat('%', #{cri.search}, '%')
     
     	
       where content like concat('%', #{cri.search}, '%')
     
      
       where writer = #{cri.search}
     
      
       where id = #{cri.search}
     
       order by id desc limit #{cri.startPage},#{cri.perPagenum};
